import styled from "styled-components";

export default styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 250px;
  width: 100%;
  /* background-color: #683bb7; */
  // background-color: orange;
  color: #fff;
  margin: 15px;
  font-size: 4em;
  border-width: 3px;
  border-style: solid;
  border-image: linear-gradient(to top, red, rgba(0, 0, 0, 0)) 1 100%;
`;
