import React, { Component } from "react";
import ReactApexChart from "react-apexcharts";

// const formCard = () => {
    
    class chart extends Component {
constructor(props) {
    super(props);
    this.state = {
      series: [
        {
          name: "DAI",
          // data: series.monthDataSeries1.prices
          data: [30, 40, 45, 50, 49, 60, 70, 91],
        },
      ],
      options: {
        chart: {
          foreColor: "white",
          type: "area",
          height: 350,
          zoom: {
            enabled: false,
          },
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          curve: "straight",
        },

        title: {
          text: "",
          align: "left",
        },
        subtitle: {
          text: "",
          align: "left",
        },
        // labels: series.monthDataSeries1.dates,
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998],
          // type: 'datetime',
        },
        yaxis: {
          // opposite: true
        },
        legend: {
          horizontalAlign: "right",
        },

        tooltip: {
          enabled: true,
          enabledOnSeries: undefined,
          shared: true,
          followCursor: false,
          intersect: false,
          inverseOrder: false,
          custom: undefined,
          fillSeriesColor: false,
          // theme: false,
          style: {
            fontSize: "12px",
            fontFamily: undefined,
            // color: "red",
          },
        },
        annotations: {
          yaxis: [
            {
              y: 60,
              borderColor: "#00E396",
              label: {
                borderColor: "#00E396",
                style: {
                  color: "#fff",
                  background: "#00E396",
                },
                text: "Current price: 1.6425 DAI",
              },
            },
          ],
          xaxis: [
            {
              // x: new Date('23 Nov 2017').getTime(),
              x: 1996,
              borderColor: "#775DD0",
              label: {
                style: {
                  color: "#fff",
                },
                // text: 'X-axis annotation - 22 Nov'
              },
            },
          ],
          points: [
            {
              x: 1996,
              y: 60,
              marker: {
                size: 8,
              },
              label: {
                borderColor: "#FF4560",
                text: "Point Annotation",
              },
            },
          ],
        },
      },
    };
  }

  render() {
    return (
      <div id="chart">
        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="area"
          height={350}
        />
      </div>
    );
  }
}

export default chart;
